 if ( ! defined( 'ABSPATH' ) ) { exit; } function fs_get_object_public_vars( $object ) { return get_object_vars( $object ); } class FS_Entity { public $id; public $updated; public $created; function __construct( $entity = false ) { if ( ! ( $entity instanceof stdClass ) && ! ( $entity instanceof FS_Entity ) ) { return; } $props = fs_get_object_public_vars( $this ); foreach ( $props as $key => $def_value ) { $this->{$key} = isset( $entity->{$key} ) ? $entity->{$key} : $def_value; } } static function get_type() { return 'type'; } static function equals( $entity1, $entity2 ) { if ( is_null( $entity1 ) && is_null( $entity2 ) ) { return true; } else if ( is_object( $entity1 ) && is_object( $entity2 ) ) { return ( $entity1->id == $entity2->id ); } else if ( is_object( $entity1 ) ) { return is_null( $entity1->id ); } else { return is_null( $entity2->id ); } } private $_is_updated = false; function update( $key, $val = false ) { if ( ! is_array( $key ) ) { $key = array( $key => $val ); } $is_updated = false; foreach ( $key as $k => $v ) { if ( $this->{$k} === $v ) { continue; } if ( ( is_string( $this->{$k} ) && is_numeric( $v ) || ( is_numeric( $this->{$k} ) && is_string( $v ) ) ) && $this->{$k} == $v ) { continue; } $this->{$k} = $v; $is_updated = true; } $this->_is_updated = $is_updated; return $is_updated; } function is_updated() { return $this->_is_updated; } static function is_valid_id($id){ return is_numeric($id); } public static function get_class_name() { return get_called_class(); } }