 if ( ! defined( 'ABSPATH' ) ) { exit; } class FS_Site extends FS_Scope_Entity { public $site_id; public $plugin_id; public $user_id; public $title; public $url; public $version; public $language; public $charset; public $platform_version; public $sdk_version; public $programming_language_version; public $plan_id; public $license_id; public $trial_plan_id; public $trial_ends; public $is_premium = false; public $is_disconnected = false; public $is_active = true; public $is_uninstalled = false; function __construct( $site = false ) { parent::__construct( $site ); if ( is_object( $site ) ) { $this->plan_id = $site->plan_id; } if ( ! is_bool( $this->is_disconnected ) ) { $this->is_disconnected = false; } } static function get_type() { return 'install'; } static function is_localhost_by_address( $url ) { if ( false !== strpos( $url, '127.0.0.1' ) || false !== strpos( $url, 'localhost' ) ) { return true; } if ( ! fs_starts_with( $url, 'http' ) ) { $url = 'http://' . $url; } $url_parts = parse_url( $url ); $subdomain = $url_parts['host']; return ( fs_starts_with( $subdomain, 'local.' ) || fs_starts_with( $subdomain, 'dev.' ) || fs_starts_with( $subdomain, 'test.' ) || fs_starts_with( $subdomain, 'stage.' ) || fs_starts_with( $subdomain, 'staging.' ) || fs_ends_with( $subdomain, '.dev' ) || fs_ends_with( $subdomain, '.test' ) || fs_ends_with( $subdomain, '.staging' ) || fs_ends_with( $subdomain, '.local' ) || fs_ends_with( $subdomain, '.example' ) || fs_ends_with( $subdomain, '.invalid' ) || fs_ends_with( $subdomain, '.myftpupload.com' ) || fs_ends_with( $subdomain, '.ngrok.io' ) || fs_ends_with( $subdomain, '.wpsandbox.pro' ) || fs_starts_with( $subdomain, 'staging' ) || fs_ends_with( $subdomain, '.staging.wpengine.com' ) || fs_ends_with( $subdomain, '.dev.wpengine.com' ) || fs_ends_with( $subdomain, '.wpengine.com' ) || ( fs_ends_with( $subdomain, 'pantheonsite.io' ) && ( fs_starts_with( $subdomain, 'test-' ) || fs_starts_with( $subdomain, 'dev-' ) ) ) || fs_ends_with( $subdomain, '.cloudwaysapps.com' ) || ( fs_starts_with( $subdomain, 'staging-' ) && ( fs_ends_with( $subdomain, '.kinsta.com' ) || fs_ends_with( $subdomain, '.kinsta.cloud' ) ) ) || fs_ends_with( $subdomain, '.dev.cc' ) || fs_ends_with( $subdomain, '.mystagingwebsite.com' ) ); } function is_localhost() { return ( WP_FS__IS_LOCALHOST_FOR_SERVER || self::is_localhost_by_address( $this->url ) ); } function is_trial() { return is_numeric( $this->trial_plan_id ) && ( strtotime( $this->trial_ends ) > WP_FS__SCRIPT_START_TIME ); } function is_trial_utilized() { return is_numeric( $this->trial_plan_id ); } function is_tracking_allowed() { return ( true !== $this->is_disconnected ); } function is_tracking_prohibited() { return ! $this->is_tracking_allowed(); } }